def _md_escape(s: str) -&gt; str:
    return s.replace("`", "\`")

for name, status, val, err in details:
    print(f"### {name.upper()} — {status}")
    if status == "OK":
        print("```")
        print(repr(val))
        print("```")
    else:
        print(f"**Error/Info:** `{_md_escape(err)}`\n")
